 /**
 *  $descript$
 * @param forward
 * @param request
 * @param response
 * @throws IOException
 * @throws ORMException
 * @throws SQLException
 */
public void $className$(ActionForward forward, HttpServletRequest request, HttpServletResponse response) throws IOException {
    UserSession session = (UserSession) RequestUtil.getSessionUser(request);
    HashMap<String, Object> map = new HashMap<>();
    HashMap<String, Object> head = new HashMap<>();
    Gson gson = new Gson();
    if (session == null) {
        head.put("code",-1);
        head.put("msg", "session过期，请重新登陆");
        log.error("session过期，请重新登陆");
        PrintWriter writer = response.getWriter();
        writer.write(gson.toJson(map));
        writer.flush();
        writer.close();
        return;
    }
    try {

            head.put("code",1);
            head.put("msg","成功");
    } catch (Exception e) {
        log.error("user[" + session.getNick() + "]，$descript$异常：" + e.toString());
        head.put("code",-1);
        head.put("msg","$descript$异常" + e.toString());
        e.printStackTrace();
    }
    map.put("head", head);
    PrintWriter out = response.getWriter();
    out.print(gson.toJson(map));
    out.flush();
    out.close();
}
